mapper.insert:
insert into SYS_ROLE_MENU (PK_ROLE_MENU, PK_ORG, PK_ROLE, 
  PK_MENU, CREATOR, CREATE_TIME, 
  MODIFIER, MODIFY_TIME, DEL_FLAG
  )
values (#{pkRoleMenu,jdbcType=VARCHAR}, #{pkOrg,jdbcType=VARCHAR}, #{pkRole,jdbcType=VARCHAR}, 
  #{pkMenu,jdbcType=VARCHAR}, #{creator,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
  #{modifier,jdbcType=VARCHAR}, #{modifyTime,jdbcType=TIMESTAMP}, #{delFlag,jdbcType=CHAR}
  )

insert into SYS_ROLE_MENU (PK_ROLE_MENU, PK_ORG, PK_ROLE, PK_MENU, CREATOR, CREATE_TIME,MODIFIER, MODIFY_TIME, DEL_FLAG) select Temp.* from (
select #{item.pkRoleMenu}, #{item.pkOrg}, #{item.pkRole}, #{item.pkMenu}, #{item.creator}, #{item.createTime}, #{item.modifier}, #{item.modifyTime}, #{item.delFlag} from dual
) Temp

mapper.select:
select count(PK_ROLE_MENU) from SYS_ROLE_MENU

     and PK_ROLE = #{roleId} 
     and PK_MENU = #{menuId}
